import { ProdutosService } from './../../../services/produtos.service';
import { IProduto } from './../../../model/IProduto.model';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-atualizar-produto',
  templateUrl: './atualizar-produto.component.html',
  styleUrls: ['./atualizar-produto.component.css']
})
export class AtualizarProdutoComponent implements OnInit {
  produto: IProduto = {
    nome: '',
    validade: new Date(),
    precoProduto: 0
  };

  constructor(
    private produtosService: ProdutosService,
    private router: Router,
    private activateRouter: ActivatedRoute) {}

  ngOnInit(): void {
    const id = Number(this.activateRouter.snapshot.paramMap.get('id'));
    this.produtosService.buscarPorId(id).subscribe(retorno => {
      this.produto = retorno;

    });

  }

  salvarProduto() : void {
    this.produtosService.atualizar(this.produto).subscribe(retorno => {
      this.produto=retorno;
      this.produtosService.exibirMensagem(
        'Sistema',
        `${this.produto.nome} foi atualizado com sucesso. ID ${this.produto.id}`,
        'toast-success'
      )
      this.router.navigate(['/produtos']);

    });
  }
}
<h1 class="my-3 text-primary">Cadastrar Produto</h1>
<div class="card shadow">
  <form class="m-3">
    <!-- Inicio row nome -->
    <div class="row">
      <div class="col md-12">
        <div class="form-group">
          <label for="nome">Nome do Produto:</label>
          <input
            type="text"
            class="form-control"
            placeholder="Digite o nome do Produto"
            name="nomeProduto"
            [(ngModel)]="produto.nome"
          />
        </div>
      </div>
    </div>
    <!-- Fim row nome -->
    <div class="row">
      <div class="col md-6">
        <div class="form-group">
          <label for="nome">Validade do Produto: </label>
          <input
            type="date"
            class="form-control"
            name="validade"
            [(ngModel)]="produto.validade"
          />
        </div>
      </div>
      <div class="col md-12">
        <div class="form-group">
          <label for="nome">Preço do Produto:</label>
          <input
            type="number"
            class="form-control"
            placeholder="0.00"
            name="preco"
            [(ngModel)]="produto.precoProduto"
          />
        </div>
      </div>
    </div>
    <div class="row">
      <div class="col">
        <button class="btn btn-default" routerLink="/produtos">Voltar</button>
        <button class="btn btn-warning float-right" (click)="salvarProduto()">Salvar</button>
      </div>
    </div>
  </form>
</div>
